//set random background
function setBackground () {
    const url = `./backgrounds/${Math.trunc(Math.random() * 12) + 1}.jpg`;
    document.querySelector('.main__container').style.backgroundImage = `url(${url})`;

    const pseudoElement = document.createElement('div');
    pseudoElement.classList.add('main__container-pseudo');
    pseudoElement.style.backgroundImage = `url(${url})`;

    document.querySelector('.main__container').appendChild(pseudoElement);
}
setBackground();


// a forecast have data and an element in dom
class ForeCast {
    node = [];
    data = [];

    constructor(node, data) {
        this.node = node;
        this.data = data;
    }
}
const foreCast = new Array(6);



// time to wait until data loaded; if after this time data has not been loaded then show an error message
let time;
let timer;

// abort from fetching data
let abortController;

let dataFetched = false;

// main containers
const leftContainer = document.querySelector('.left__side');
const rightContainer = document.querySelector('.right__side');
const bottomContainer = document.querySelector('.bottom');
const cityContainer = document.querySelector('.weather__In');

// select elements
const searchBtn = document.querySelector('.search__btn');
const searchInput = document.querySelector('input');
const cityLabel = document.querySelector('.city__text p');
const currSvgContainer = document.querySelector('.status__ico');
const currStatusLabel = document.querySelector('.status__txt span');
const currTemp = document.querySelector('.temp__container');
const humidity = document.querySelector('.humidity__content');
const pressure = document.querySelector('.pressure__content');
const feelsLike = document.querySelector('.chance__content');
const windSpeed = document.querySelector('.windSpeed__content');
const next5DaysContainer = document.querySelector('.forecast__days');
const loader = document.querySelector('.spinner');


// after all elements loaded then show them
window.addEventListener('load', function () {
    document.body.style.opacity = 1;
});

// start the time for fetching data
function startTimer(t) {
    time = t;
    clearInterval(timer);
    timer = setInterval(function () {

        time--;
        if (time < 0 && !dataFetched) {
            abortController.abort();
            clearInterval(timer);
        } else if (time < 0)
            clearInterval(timer);


    }, 1000);
}


// setting data for next 5 days forecast and initial forecast array object
function setForecast(forecastData) {
    next5DaysContainer.innerHTML = "";
    for (const [i, dataItem] of Object.entries(forecastData)) {

        let date;
        if (i == 0) {
            date = 'Today';
        } else if (i == 1) {
            date = 'Tomorrow';
        } else {
            const splitedDate = `${(new Date(dataItem.dt_txt))}`.split(' ');
            date = `${splitedDate[0]}, ${splitedDate[1]} ${splitedDate[2]}`;
        }
        const html = `
        <button class="forecast">
            <div class="date">
                <p>${date}</p>
            </div>
            <div class="status__svg">
                ${svgMap.get(dataItem.weather[0].main.toLowerCase())}
            </div>
            <div class="day__temp"><p>${(+dataItem.main.temp - 273.15).toFixed(0)} °C</p></div>
        </button>
        `;
        next5DaysContainer.insertAdjacentHTML('beforeend', html);

        foreCast[i] = new ForeCast(next5DaysContainer.lastElementChild, dataItem);
    }

    // if clicked on a forecast day
    for (const item of foreCast) {
        item.node.addEventListener('click', function () {

            foreCast.forEach(function (itemF) {
                itemF.node.style.transform = 'scale(1)';
            })
            item.node.style.transform = 'scale(1.1)'
            showCurrentWeather(item.data, 'forecast');

        })
    }

}


// show today weather data
function showCurrentWeather(data, dist = 'usually') {

    if (dist !== 'forecast')
        bottomContainer.style.animation = 'hide 0.5s linear';

    leftContainer.style.animation = 'hide 0.5s linear';
    rightContainer.style.animation = 'hide 0.5s linear';

    // after 0.5s data's show
    setTimeout(function () {
        if (dist !== 'forecast')
            bottomContainer.style.animation = 'show0 0.5s linear';

        leftContainer.style.animation = 'show 0.5s linear';
        rightContainer.style.animation = 'show 0.5s linear';

        currSvgContainer.innerHTML = svgMap.get(data.weather[0].main.toLowerCase());
        currStatusLabel.innerHTML = data.weather[0].main;
        currTemp.innerHTML = (+data.main.temp - 273.15).toFixed(0) + '°C';
        humidity.innerHTML = data.main.humidity + ' %';
        pressure.innerHTML = data.main.pressure;
        feelsLike.innerHTML = (+data.main.feels_like - 273.15).toFixed(0) + '°C';
        windSpeed.innerHTML = data.wind.speed + ' km/h';

    }, 500)
}

// main containers before search have zero opacity, after search a city these containers show
function setVisibility(visibility) {
    leftContainer.style.visibility = visibility;
    rightContainer.style.visibility = visibility;
    bottomContainer.style.visibility = visibility;
}

// after fetching data, show today weather and set forecast
function renderData(data, cityName) {
    cityContainer.style.visibility = 'visible';
    dataFetched = true;
    loader.style.display = 'none';
    clearInterval(timer);
    searchInput.value = '';


    data.newList = data.list.filter(item => item !== null);


    cityLabel.innerHTML = `Weather in ${cityName.charAt(0).toUpperCase() + cityName.slice(1, cityName.length)}`;
    showCurrentWeather(data.newList[0]);

    setTimeout(function () {
        setForecast(data.newList);
        setVisibility('visible');
    }, 500);

}


// get data from api
function fetchData() {
    if (!searchInput.value) return;

    startTimer(10);

    // show loader
    if (getComputedStyle(loader).display === 'none')
        loader.style.display = 'flex';


    abortController = new AbortController()
    const cityName = searchInput.value.toLowerCase();
    const APIKey = '20570e3ef4bf97e8f0f9e1619127976f';
    const locationUrl = `https://api.openweathermap.org/geo/1.0/direct?q=${cityName}&limit=1&appid=${APIKey}`;
    const fetchOptions = {
        method: 'GET',
        signal: abortController.signal
    };

    // fetching location latitude and longitude
    fetch(locationUrl, fetchOptions)
        .then(locResponse => locResponse.json())
        .then(locData => {

            const weatherUrl = `https://api.openweathermap.org/data/2.5/forecast?lat=${locData[0].lat}&lon=${locData[0].lon}&appid=${APIKey}`;

            // fetching weather data based on the location
            fetch(weatherUrl, fetchOptions)
                .then(weatherResponse => weatherResponse.json())
                .then(weatherData => {


                    weatherData.list = weatherData.list.map(function (item) {
                        item.dt_txt = item.dt_txt.split(' ')[0];
                        return item;
                    })

                    // remove repeated data
                    let current = '';
                    for (let i = 0; i < weatherData.list.length; i++)
                        current !== weatherData.list[i].dt_txt ? current = weatherData.list[i].dt_txt : weatherData.list[i] = null;

                    renderData(weatherData, cityName);


                }).catch(handleError)
        }).catch(handleError);
}


// handle errors while fetching data may occur
function handleError(er) {
    // check witch error is occurs

    if (er.message.includes('Failed to fetch') || er.message.includes('The user aborted a request')) {
        cityLabel.innerHTML = 'Not Connected';
    } else if (er.message.includes('Cannot read properties of undefined')) {
        cityLabel.innerHTML = 'There\'s no such city';
    }

    loader.style.display = 'none';
    dataFetched = false;
    cityContainer.style.visibility = 'visible';

    // if error occurs, then hide all main containers
    setVisibility('hidden');

}


// send data for fetching

searchBtn.addEventListener('click', fetchData)
searchInput.addEventListener('keydown', function (ev) {
    if (ev.key === 'Enter')
        fetchData();
})

